// TestBulkInsertDuplicates.
func testBulkInsertDuplicates(t *testing.T, ctr *Connector, db *sql.DB) {
	table := RandomIdentifier("bulkInsertDuplicates")

	if _, err := db.ExecContext(t.Context(), fmt.Sprintf("create table %s (k integer primary key, v integer)", table)); err != nil {
		t.Fatalf("create table failed: %s", err)
	}

	stmt, err := db.PrepareContext(t.Context(), fmt.Sprintf("insert into %s values (?,?)", table))
	if err != nil {
		t.Fatalf("prepare bulk insert failed: %s", err)
	}
	defer stmt.Close()

	// insert 3 rows (ids: 1,2,3)
	i := 1
	if _, err := stmt.Exec(func(args []any) error {
		if i >= 4 {
			return ErrEndOfRows
		}
		args[0], args[1] = i, i
		i++
		return nil
	}); err != nil {
		t.Fatal(err)
	}

	// insert 5 rows (ids: 0,1,2,3,4) with 3 duplicates (ids: 1,2,3)
	i = 0
	_, err = stmt.Exec(func(args []any) error {
		if i >= 5 {
			return ErrEndOfRows
		}
		args[0], args[1] = i, i
		i++
		return nil
	})
	if err == nil {
		t.Fatal("error duplicate key expected")
	}

	var hdbErr Error
	if !errors.As(err, &hdbErr) {
		t.Fatal("driver.Error expected")
	}

	// expect 3 errors for statement 1,2 and 3
	if hdbErr.NumError() != 3 {
		t.Fatalf("number of errors: %d - %d expected", hdbErr.NumError(), 3)
	}

	stmtNo := []int{1, 2, 3}

	for i := range hdbErr.NumError() {
		hdbErr.SetIdx(i)
		if hdbErr.StmtNo() != stmtNo[i] {
			t.Fatalf("statement number: %d - %d expected", hdbErr.StmtNo(), stmtNo[i])
		}
	}
}

// TestBulkInsertStmtNo.
func testBulkInsertStmtNo(t *testing.T, ctr *Connector, db *sql.DB) {
	bulkSize := ctr.BulkSize()

	table := RandomIdentifier("bulkInsertDuplicates")

	if _, err := db.ExecContext(t.Context(), fmt.Sprintf("create table %s (k integer primary key, v integer)", table)); err != nil {
		t.Fatalf("create table failed: %s", err)
	}

	stmt, err := db.PrepareContext(t.Context(), fmt.Sprintf("insert into %s values (?,?)", table))
	if err != nil {
		t.Fatalf("prepare bulk insert failed: %s", err)
	}
	defer stmt.Close()

	// insert -1 and defaultBulkSize+1 (duplicate)
	duplID := bulkSize + 1
	args := []any{-1, -1, duplID, duplID}
	if _, err := stmt.Exec(args...); err != nil {
		t.Fatal(err)
	}

	// insert one row with duplicate
	args = []any{duplID, duplID}
	_, err = stmt.Exec(args...)
	if err == nil {
		t.Fatal("driver.error expected")
	}

	var hdbErr Error
	if !errors.As(err, &hdbErr) {
		t.Fatal("driver.Error expected")
	}

	// check, that StmtNo matches counter
	if hdbErr.StmtNo() != 0 {
		t.Fatalf("actual StmtNo %d - expected StmtNo %d", hdbErr.StmtNo(), 0)
	}

	// insert bulk data with duplicate > defaultBulkSize
	numRow := bulkSize + 2
	args = make([]any, numRow*2)
	for i := range numRow {
		args[i*2], args[i*2+1] = i, i
	}
	_, err = stmt.Exec(args...)
	if err == nil {
		t.Fatal("driver.error expected")
	}

	if !errors.As(err, &hdbErr) {
		t.Fatal("driver.Error expected")
	}

	// check, that StmtNo matches counter (which equals duplID in this case)
	if hdbErr.StmtNo() != duplID {
		t.Fatalf("actual StmtNo %d - expected StmtNo %d", hdbErr.StmtNo(), duplID)
	}
}

// TestBulkBlob.
func testBulkBlob(t *testing.T, ctr *Connector, db *sql.DB) {
	const numRows = 100
	chunkSize := ctr.LobChunkSize()
	bigData := strings.Repeat("a", chunkSize)

	smallLobData := func(i int) string {
		return fmt.Sprintf("%s-%d", "Go rocks", i)
	}

	bigLobData := func(i int) string {
		return fmt.Sprintf("%s-%d", bigData, i)
	}

	tmpTableName := RandomIdentifier("#tmpTable")

	// keep connection / hdb session for using local temporary tables
	tx, err := db.BeginTx(t.Context(), nil)
	if err != nil {
		t.Fatal(err)
	}
	// cleanup
	defer tx.Rollback() //nolint: errcheck

	if _, err := tx.Exec(fmt.Sprintf("create local temporary table %s (i integer, b1 blob, b2 blob)", tmpTableName)); err != nil {
		t.Fatalf("create table failed: %s", err)
	}

	stmt, err := tx.Prepare(fmt.Sprintf("insert into %s values (?, ?, ?)", tmpTableName))
	if err != nil {
		t.Fatalf("prepare bulk insert failed: %s", err)
	}
	defer stmt.Close()

	// call insert function
	i := 0
	if _, err := stmt.Exec(func(args []any) error {
		if i >= numRows {
			return ErrEndOfRows
		}
		args[0], args[1], args[2] = i, bigLobData(i), smallLobData(i)
		i++
		return nil
	}); err != nil {
		t.Fatal(err)
	}

	// check
	err = tx.QueryRow(fmt.Sprintf("select count(*) from %s", tmpTableName)).Scan(&i)
	if err != nil {
		t.Fatalf("select count failed: %s", err)
	}

	if i != numRows {
		t.Fatalf("invalid number of records %d - %d expected", i, numRows)
	}

	rows, err := tx.Query(fmt.Sprintf("select * from %s order by i", tmpTableName))
	if err != nil {
		t.Fatal(err)
	}
	defer rows.Close()

	var stringLob1, stringLob2 stringLob // defined in lob_test
	i = 0
	for rows.Next() {
		var j int
		if err := rows.Scan(&j, &stringLob1, &stringLob2); err != nil {
			t.Fatal(err)
		}
		if j != i {
			t.Fatalf("value %d - expected %d", j, i)
		}
		if string(stringLob1) != bigLobData(i) {
			t.Fatalf("value %s - expected %s", stringLob1, bigLobData(i))
		}
		if string(stringLob2) != smallLobData(i) {
			t.Fatalf("value %s - expected %s", stringLob2, smallLobData(i))
		}
		i++
	}
	if err := rows.Err(); err != nil {
		t.Fatal(err)
	}
}

func testBulkBlob106(t *testing.T, ctr *Connector, db *sql.DB) {
	/*
		issue https://github.com/SAP/go-hdb/issues/106
		precondition:
			- bulk insert of blob data
			- most of the blob content does fit into lob chunk size
			- only some of the blob content did exceed lob chunk size
	*/

	tableName := RandomIdentifier("bulkBlob106")

	if _, err := db.ExecContext(t.Context(), fmt.Sprintf("create table %s (i integer, b nclob)", tableName)); err != nil {
		t.Fatalf("create table failed: %s", err)
	}

	const (
		numRecs           = 1000
		numRecsPerCall    = numRecs / 10
		bigChunkSizeRecNo = 77 // record exceeding lob chunk size
	)

	chunkSize := MT.Connector().LobChunkSize()

	testData := [numRecsPerCall]string{}

	for i := range numRecsPerCall {
		if i == bigChunkSizeRecNo {
			testData[i] = strings.Repeat("a", chunkSize+1)
		} else {
			testData[i] = "b"
		}
	}

	tx, err := db.BeginTx(t.Context(), nil)
	if err != nil {
		t.Fatal(err)
	}

	stmt, err := tx.PrepareContext(t.Context(), fmt.Sprintf("insert into %s values (?, ?)", tableName))
	if err != nil {
		t.Fatal(err)
	}
	defer stmt.Close()

	args := make([]any, 2*numRecsPerCall)

	for i := range numRecs / numRecsPerCall {
		for j := range numRecsPerCall {
			args[j*2] = i*numRecsPerCall + j
			args[j*2+1] = testData[j]
		}
		if _, err := stmt.Exec(args...); err != nil {
			t.Fatal(err)
		}
	}
	if err := tx.Commit(); err != nil {
		t.Fatal(err)
	}

	// check
	i := 0
	err = db.QueryRowContext(t.Context(), fmt.Sprintf("select count(*) from %s", tableName)).Scan(&i)
	if err != nil {
		t.Fatalf("select count failed: %s", err)
	}

	if i != numRecs {
		t.Fatalf("invalid number of records %d - %d expected", i, numRecs)
	}

	rows, err := db.QueryContext(t.Context(), fmt.Sprintf("select * from %s order by i", tableName))
	if err != nil {
		t.Fatal(err)
	}
	defer rows.Close()

	var stringLob stringLob // defined in lob_test
	i = 0
	for rows.Next() {
		var j int
		if err := rows.Scan(&j, &stringLob); err != nil {
			t.Fatal(err)
		}
		if j != i {
			t.Fatalf("value %d - expected %d", j, i)
		}
		if string(stringLob) != testData[j%numRecsPerCall] {
			t.Fatalf("value %s len %d - expected %s len %d",
				stringLob,
				len(string(stringLob)),
				testData[j%numRecsPerCall],
				len(testData[j%numRecsPerCall]))
		}
		i++
	}
	if err := rows.Err(); err != nil {
		t.Fatal(err)
	}
}

func testBulkGeo(t *testing.T, ctr *Connector, db *sql.DB) {
	const (
		ewkb    = ""
		numRows = 10
	)

	tableName := RandomIdentifier("bulkGeo")

	if _, err := db.ExecContext(t.Context(), fmt.Sprintf("create table %s (id int, geo st_geometry(3857))", tableName)); err != nil {
		t.Fatalf("create table failed: %s", err)
	}

	stmt, err := db.PrepareContext(t.Context(), fmt.Sprintf("insert into %s values (?, st_geomfromewkb(?))", tableName)) // Prepare bulk query.
	if err != nil {
		t.Fatal(err)
	}
	defer stmt.Close()

	i := 0
	if _, err := stmt.Exec(func(args []any) error {
		if i >= numRows {
			return ErrEndOfRows
		}
		args[0], args[1] = i, ewkb
		i++
		return nil
	}); err != nil {
		t.Fatal(err)
	}
	/*
		see https://github.com/SAP/go-hdb/issues/71
		- read rows to double check that geometry field attributes can be read
		- protocol return type is tcStGeometry (not tcLocator)
	*/
	rows, err := db.QueryContext(t.Context(), fmt.Sprintf("select * from %s order by id", tableName))
	if err != nil {
		t.Fatal(err)
	}
	defer rows.Close()

	i = 0
	for rows.Next() {
		var j int
		b := []byte{}

		if err := rows.Scan(&j, &b); err != nil {
			t.Fatal(err)
		}
		if j != i {
			t.Fatalf("value %d - expected %d", j, i)
		}
		// t.Logf("value: %[1]v%[1]s", b)
		i++
	}
	if err := rows.Err(); err != nil {
		t.Fatal(err)
	}
}

func testBulkInsertInvalidUTF8(t *testing.T, ctr *Connector, db *sql.DB) {
	// test, that bulk insert returns transformation errors.
	var invalidUTF8 = string([]byte{0xed, 0xa2, 0xa8})

	tableName := RandomIdentifier("bulkInvalidUTF8")

	// Create table.
	if _, err := db.Exec(fmt.Sprintf("create table %s (s nvarchar(123))", tableName)); err != nil {
		t.Fatal(err)
	}

	// Prepare statement.
	stmt, err := db.PrepareContext(t.Context(), fmt.Sprintf("insert into %s values (?)", tableName))
	if err != nil {
		t.Fatal(err)
	}
	defer stmt.Close()

	// Bulk insert via function.
	i := 0
	if _, err = stmt.Exec(func(args []any) error {
		if i >= 1 {
			return ErrEndOfRows
		}
		args[0] = invalidUTF8
		i++
		return nil
	}); err == nil {
		t.Fatal("expected error")
	}
}

func TestBulk(t *testing.T) {
	t.Parallel()

	tests := []struct {
		name string
		fct  func(t *testing.T, ctr *Connector, db *sql.DB)
	}{
		{"testBulkInsertDuplicates", testBulkInsertDuplicates},
		{"testBulkInsertStmtNo", testBulkInsertStmtNo},
		{"testBulkBlob", testBulkBlob},
		{"testBulkBlob106", testBulkBlob106},
		{"testBulkGeo", testBulkGeo},
		{"testBulkInsertInvalidUTF8", testBulkInsertInvalidUTF8},
	}

	ctr := MT.NewConnector()
	ctr.setBulkSize(1000) // limit bulk size for test performance reasons
	db := sql.OpenDB(ctr)
	t.Cleanup(func() { db.Close() })

	for _, test := range tests {
		t.Run(test.name, func(t *testing.T) {
			t.Parallel()
			test.fct(t, ctr, db)
		})
	}
}
